export async function saveAssessmentResults() {
    const user = auth.currentUser;
    if (!user) {
        alert("Please log in first!");
        window.location.href = 'login.html';
        return;
    }

    const age = parseInt(localStorage.getItem('userAge') || '20');
    const ageGroup = getAgeGroup(age);
    const discAnswers = getUserAnswers();

    if (Object.keys(discAnswers).length === 0) {
        alert("Please answer all questions.");
        return;
    }

    const scores = calculateDISCScores(discAnswers, ageGroup);

    const assessmentData = {
        discAnswers,
        D: scores.D,
        I: scores.I,
        S: scores.S,
        C: scores.C,
        ageGroup,
        assessmentCompleted: true,
        timestamp: new Date().toISOString()
    };

    try {
        const userRef = doc(db, 'users', user.uid);
        await setDoc(userRef, { assessmentData }, { merge: true });
        console.log("SUCCESS: Assessment saved for UID:", user.uid);
        window.location.href = 'dashboard.html';
    } catch (err) {
        console.error("FAILED TO SAVE:", err);
        alert("Save failed. Check internet or console.");
    }
}



